import { useState } from "react";
import axios from "axios";

const TestElement = () => {
  const [formValues, setFormValues] = useState({
    name: "",
    units: "",
    quantity: "",
    texture: "",
    productImageUrl: "",
    price: "",
    description: "",
  });

  const handleChange = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let res = await axios.post("http://localhost:3001/api/products", {
      name: formValues.name,
      units: formValues.units,
      quantity: formValues.quantity,
      texture: formValues.texture,
      productImageUrl: formValues.productImageUrl,
      price: formValues.price,
      description: formValues.description,
    });
    console.log(res);
  };

  return (
    <div className="add-product">
      <form className="col" onSubmit={handleSubmit}>
        <div className="input-wrapper">
          {/* <label htmlFor="name">~~name~~</label> */}
          <input
            onChange={handleChange}
            name="name"
            type="text"
            placeholder="Product name"
            value={formValues.name}
            required
          />
        </div>
        <div className="input-wrapper">
          <input
            onChange={handleChange}
            name="units"
            type="text"
            placeholder="Units"
            value={formValues.units}
            required
          />
        </div>
        <div className="input-wrapper">
          <input
            onChange={handleChange}
            name="quantity"
            type="text"
            placeholder="Quantity"
            value={formValues.quantity}
            required
          />
        </div>
        <div className="input-wrapper">
          <input
            onChange={handleChange}
            name="texture"
            type="text"
            placeholder="texture"
            value={formValues.texture}
            required
          />
        </div>
        <div className="input-wrapper">
          <input
            onChange={handleChange}
            name="productImageUrl"
            type="text"
            placeholder="Image Url"
            value={formValues.productImageUrl}
            required
          />
        </div>
        <div className="input-wrapper">
          <input
            onChange={handleChange}
            name="price"
            type="text"
            placeholder="Price"
            value={formValues.price}
            required
          />
        </div>
        <div className="input-wrapper">
          <input
            onChange={handleChange}
            name="description"
            type="text"
            placeholder="Description"
            value={formValues.description}
            required
          />
        </div>
        <button disabled={!formValues.name}>Add</button>
      </form>
    </div>
  );
};

export default TestElement;
